
public class Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		final int LIMIT = 100; // setup
		int count = 1;
		while (count <= LIMIT) // condition
		{ // body
		 // -- perform task
		count = count + 1; // -- update condition
		System.out.println(count);
		}

	}

}
